import React from "react";
import Card from "./cards/Card";
import FundoService from '../images/FundoService.png';
import { services } from '../static-data-cards';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

function ServicesPage() {

    const { ref, inView } = useInView({
        threshold: 0.1,     // Ativa quando 10% da seção está visível
    });

    return (
        <div className=" bg-center bg-cover min-h-screen flex flex-col items-center justify-center overflow-x-hidden" style={{ backgroundImage: `url(${FundoService})` }}>

            <div ref={ref} className="z-20">
            <motion.div
                initial={{ opacity: 0, y: -80 }}
                animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: -80 }}
                transition={{ duration: 4 }}
            >
                <div className="w-full h-auto">
                    <h1 className="font-[Poppins] md:text-5xl md:text-start text-center text-2xl md:pt-0 pt-7 font-semibold">
                        Soluções oferecidas pela <span className="text-magic-300 font-bold">Magic</span>
                    </h1>
                </div>
            </motion.div>

                <div className="w-auto md:h-96 h-[800px] justify-center items-center flex md:flex-row flex-col gap-16 z-10">
                    {services.map((service, i) => (
                        <Card
                            key={i}
                            icon={service.icon}
                            title={service.title}
                            description={service.description}
                        />
                    ))}
                </div>
            </div>
        </div>
    );

}

export default ServicesPage;
