import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import imagem1 from '../images/carroussel/MagicCarroussel.png'
import imagem2 from '../images/carroussel/Certificado.png'
import imagem3 from '../images/carroussel/FotoParede.png'

function AboutPage() {
    const { ref, inView } = useInView({
        triggerOnce: true, // A animação ocorre apenas uma vez
        threshold: 0.2,    // Inicia a animação quando 20% do elemento estiver visível
    });

    const typingEffect = {
        hidden: { opacity: 1 },
        visible: (i) => ({
            opacity: 1,
            transition: {
                delayChildren: i * 0.02,
                staggerChildren: 0.02,
            },
        }),
    };

    const characterVariant = {
        hidden: { opacity: 0 },
        visible: { opacity: 1 },
    };

    const text1 = "O estúdio Magic é uma das soluções B2B oferecidas pela Codi Academy, o estúdio é composto pelos melhores alunos da instituição, que juntos constroem tecnologia para empresas parceiras.";
    const text2 = "Já a Codi Academy é a escola de programação do Centro Regional de Inovação e Transferência de Tecnologia (CRITT) da UFJF. Nossa missão é criar novos talentos para o mercado da Tecnologia.";

    const settings = {
        dots: true, // Mostra pontos de navegação
        infinite: true, // Loop infinito
        speed: 500, // Velocidade de transição
        slidesToShow: 1, // Mostra uma imagem por vez
        slidesToScroll: 1, // Desliza uma imagem por vez
        autoplay: true, // Ativa a transição automática
        autoplaySpeed: 3000, // Tempo em milissegundos para a transição automática
        arrows: false, // Desativa as setas de navegação
    };

    return (
        <section className="md:h-screen h-auto w-full bg-gradient-to-b from-magic-300 to-magic-500 flex items-center justify-center gap-20 md:p-20 p-14 flex-col">
            <div ref={ref}  className=" text-center py-1 text-4xl font-medium text-white bg-magic-500 w-72 rounded-full border-2 border-white font-[Poppins]">Sobre nós</div>
            <div className="flex items-center justify-center md:gap-36 gap-0 md:flex-row flex-col"> 
                <div className="border-4 rounded-xl h-auto w-80 border-offwhite">
                    <Slider {...settings} className="h-full w-full">
                        <img src={imagem1} alt="Imagem 1" className="h-full w-full object-cover rounded-lg" />
                        <img src={imagem2} alt="Imagem 1" className="h-full w-full object-cover rounded-lg" />
                        <img src={imagem3} alt="Imagem 1" className="h-full w-full object-cover rounded-lg" />
                    </Slider>
                </div>
                <div className="md:h-96 h-screen flex flex-col justify-center md:w-[55%] w-full md:gap-16 gap-6 md:items-start items-center">
                    
                    <div className="text-white text-justify text-lg mt-4 font-[Poppins] flex flex-col gap-4">
                        {/* Primeiro Parágrafo */}
                        <motion.p
                            initial="hidden"
                            animate={inView ? "visible" : "hidden"}
                            variants={typingEffect}
                            custom={0}
                            className="text-justify"
                        >
                            {text1.split("").map((char, index) => (
                                <motion.span key={index} variants={characterVariant}>
                                    {char}
                                </motion.span>
                            ))}
                        </motion.p>
                        
                        {/* Segundo Parágrafo */}
                        <motion.p
                            initial="hidden"
                            animate={inView ? "visible" : "hidden"}
                            variants={typingEffect}
                            custom={text1.length + 1}
                            className="text-justify"
                        >
                            {text2.split("").map((char, index) => (
                                <motion.span key={index} variants={characterVariant}>
                                    {char}
                                </motion.span>
                            ))}
                        </motion.p>
                    </div>
                </div>
            </div>
           
        </section>
    );
}

export default AboutPage;
