import React from 'react';
import { motion } from 'framer-motion';

function AboutPage() {
    const typingEffect = {
        hidden: { opacity: 1 },
        visible: (i) => ({
            opacity: 1,
            transition: {
                delayChildren: i * 0.04,
                staggerChildren: 0.01,
            },
        }),
    };

    const characterVariant = {
        hidden: { opacity: 0 },
        visible: { opacity: 1 },
    };

    const text1 = "O estúdio Magic é uma das soluções B2B oferecidas pela Codi Academy, o estúdio é composto pelos melhores alunos da instituição, que juntos constroem tecnologia para empresas parceiras.";
    const text2 = "Já a Codi Academy é a escola de programação do Centro Regional de Inovação e Transferência de Tecnologia (CRITT) da UFJF. Nossa missão é criar novos talentos para o mercado da Tecnologia.";

    return (
        <section className="h-screen w-full bg-gradient-to-b from-magic-300 to-magic-500 flex items-center justify-center gap-36 p-20 ">
            <div className="border-4 rounded-xl h-96 w-72 border-offwhite"></div>
            <div className="h-96 flex flex-col justify-center w-[55%] gap-20">
                <div className=" text-center py-1 text-3xl font-bold text-white bg-magic-500 w-56 rounded-full border-2 border-white font-[Poppins]">Sobre</div>
                <div className="text-white text-justify text-lg mt-4 font-[Poppins] flex flex-col gap-4">
                    <motion.p
                        initial="hidden"
                        animate="visible"
                        variants={typingEffect}
                        custom={0}
                        className="text-justify"
                    >
                        {text1.split("").map((char, index) => (
                            <motion.span key={index} variants={characterVariant}>
                                {char}
                            </motion.span>
                        ))}
                    </motion.p>
                    <motion.p
                        initial="hidden"
                        animate="visible"
                        variants={typingEffect}
                        custom={text1.length + 1} // Começa após o primeiro parágrafo
                        className="text-justify"
                    >
                        {text2.split("").map((char, index) => (
                            <motion.span key={index} variants={characterVariant}>
                                {char}
                            </motion.span>
                        ))}
                    </motion.p>
                </div>
            </div>
        </section>
    );
}

export default AboutPage;
